import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class SecureServlet {

    public void handleRequest(HttpServletRequest request, HttpServletResponse response) throws IOException {
        // Read the request body
        BufferedReader reader = request.getReader();
        String input = "";
        while (reader.readLine() != null) {
            input += reader.readLine();
        }

        // Secure logic:
        // Validate the input to ensure it contains the expected format
        if (input.contains("username=") && input.substring(input.indexOf("username=") + 6).length() > 0) {
            String username = input.substring(input.indexOf("username=") + 6);
            // ... further processing
        } else {
            // Handle invalid input
            response.sendError(HttpServletResponse.SC_BAD_REQUEST);
        }
    }
}
```

