                // Read the file contents
                String contents = new String(file.readAllBytes());
                System.out.println(contents);
            } else {
                System.out.println("File not found.");
            }
        } catch (IOException e) {
            System.out.println("Error reading file: " + e.getMessage());
        }
    }
}
